import '../styles/Select.css'

function Select(props){
    function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;
        const formData = new FormData(form);
        const value = Object.fromEntries(formData.entries());

        // Consulta 
        const requestInit = {
            method: 'POST',
            headers: { 
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            },
            body: JSON.stringify(value)
        }

        fetch('http://34.94.205.206:9000/api/machine', requestInit)
        .then(res => res.json())
        .then(res => props.SetData(res))

        fetch('http://34.94.205.206:9000/api/process', requestInit)
        .then(res => res.json())
        .then(res => props.SetProcesos(res))
    }

    if (props.Type === 1){
        return (
            <form method="POST" onSubmit={handleSubmit}>
            <div id='list-button'>
                <select id='choices' name='choices'>
                <option value="0" disabled="yes"> Seleccione una opción </option>
                { props.Data.map( c => (
                    <option key={c.nombre_curso} value={c.nombre_curso}>{c.nombre_curso}</option>
                ))}
                </select>
            </div> 
            </form>
        );
    }

    if (props.Type === 2){
        return (
            <form method="POST" onSubmit={handleSubmit}>
            <div id='list-button'>
                <select id='choices' name='choices'>
                <option value="0" disabled="yes"> Seleccione una opción </option>
                { props.Data.map( a => (
                    <option key={a.año} value={a.año}>{a.año}</option>
                ))}
                </select>
                <button type='submit' className='button-select'> Seleccionar </button>
            </div> 
            </form>
        );
    }

    if (props.Type === 3){
        return (
            <form method="POST" onSubmit={handleSubmit}>
            <div id='list-button'>
                <select id='choices' name='choices'>
                <option value="0" disabled="yes"> Seleccione una opción </option>
                { props.Data.map( s => (
                    <option key={s.semestre} value={s.semestre}>{s.semestre}</option>
                ))}
                </select>
            </div> 
            </form>
        );
    }
}

export default Select;
