import { useState, useEffect, Fragment } from 'react';
import './App.css';
import { Routes, Route } from 'react-router-dom';
import PageHeader from './components/PageHeader';
import DataTable from './components/DataTable';
import Graphs from './components/Graphs';

function App() {

  // Estado para almacenar los datos que estarán en la tabla
  const [scores, setScores] = useState([])
  const [courses, setCourses] = useState([])
  const [years, setYears] = useState([])
  const [semesters, setSemesters] = useState([])
  const [average, setAverage] = useState([])
  const [mostStudents, setMostStudents] = useState([])

  useEffect(() => {
    const getScores = async () => {
      const requestInit = {
        Method: 'GET', //Podría de method con minúscula
        Headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } //podría ser headers con minúscula
      }

      try {
        const response = await fetch('http://localhost:9000/api/getData', requestInit);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setScores(data);
      } catch (error) {
        console.error('No se pudo obtener la data:', error);
      }
    }

    const getCourses = async () => {
      const requestInit = {
        Method: 'GET', //Podría de method con minúscula
        Headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } //podría ser headers con minúscula
      }

      try {
        const response = await fetch('http://localhost:9000/api/getCourses', requestInit);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('No se pudo obtener la data:', error);
      }
    }

    const getYears = async () => {
      const requestInit = {
        Method: 'GET', //Podría de method con minúscula
        Headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } //podría ser headers con minúscula
      }

      try {
        const response = await fetch('http://localhost:9000/api/getYears', requestInit);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setYears(data);
      } catch (error) {
        console.error('No se pudo obtener la data:', error);
      }
    }

    const getSemesters = async () => {
      const requestInit = {
        Method: 'GET', //Podría de method con minúscula
        Headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } //podría ser headers con minúscula
      }

      try {
        const response = await fetch('http://localhost:9000/api/getSemesters', requestInit);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setSemesters(data);
      } catch (error) {
        console.error('No se pudo obtener la data:', error);
      }
    }

    getScores();
    getCourses();
    getYears();
    getSemesters();
  }, []);



  return (
    <Fragment>
      <div className="App">
        <Routes>
          <Route path='/' element={<PageHeader />}>
            <Route path='TiempoReal' element={
              <div className='contenedor'>

              </div>
            } />
            <Route path='/' element={
              <div className='contenedor'>
                <br /><br /><br />
                <DataTable
                  Data={scores} />
                <Graphs
                  Courses = {courses}
                  Years = {years}
                  Semesters = {semesters} 
                  SetAverage = {setAverage}
                  SetStudents = {setMostStudents}/>
              </div>
            } />
          </Route>
        </Routes>
      </div>
    </Fragment>
  );
}

export default App;
