import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    BarElement,
    Title,
    Tooltip,
    Legend,
    Filler,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    BarElement,
    Title,
    Tooltip,
    Legend,
    Filler
);

function BarsChart(props) {

    var options = {
        responsive : true,
        animation : false,
        plugins : {
            legend : {
                display : false
            }
        },
        scales : {
            y : {
                min : 0,
                max : 100
            },
            x: {
                ticks: { color: 'rgba(0, 220, 195)'}
            }
        }
    };

    var data = {
        labels: props.Data.nombre,
        datasets: [
            {
                label: 'Mejores promedios',
                data: props.Data.promedio,
                backgroundColor: 'rgba(0, 220, 195, 0.5)'
            }
        ]
    };


    return <Bar data={data} options={options} />
}

export default BarsChart;
